// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.9;

// Uncomment this line to use console.log
// import "hardhat/console.sol";
interface IERC20 {
    function transfer(
        address recipient,
        uint256 amount
    ) external returns (bool);

    function transferFrom(
        address from,
        address to,
        uint256 amount
    ) external returns (bool);
}

contract TokenBridge {
    event BridgeRelease(
        uint256 amount,
        address destinationWallet,
        address token,
        // keccak256 of transaction hash on source chain
        // for solana => base 58 to bytes then keccak256 (https://docs.solana.com/developing/programming-model/transactions#transaction-format) (https://docs.solana.com/integrations/exchange#validating-user-supplied-account-addresses-for-withdrawals)
        // for evm => direct(no decoding) keccak256 (https://github.com/ethereum/EIPs/issues/55)
        // for algorand => base 32 to bytes then keccak256 (https://developer.algorand.org/docs/get-details/encoding/#base64)
        // for flow => direct(no decoding) keccak256
        bytes32 depositTransactionHash
    );

    modifier onlyAdmin() {
        require(msg.sender == admin, "only callable by admin");
        _;
    }

    address public admin;

    constructor(address _admin) {
        admin = _admin;
    }

    /**
     * To be called only by our
     * middle address on the respective EVM
     * chain, needs ERC20 allowance
     */
    function release(
        uint256 amount,
        address destinationWallet,
        address erc20Address,
        bytes32 depositTransactionHash
    ) public onlyAdmin {
        IERC20(erc20Address).transferFrom(
            msg.sender,
            destinationWallet,
            amount
        );

        emit BridgeRelease(
            amount,
            destinationWallet,
            erc20Address,
            depositTransactionHash
        );
    }
}
